import React from 'react';
import data from './data';
import {BrowserRouter, Route} from 'react-router-dom'
import './App.css';
import HomeScreen from './Screens/HomeScreen';
import MenuScreen from './Screens/MenuScreen';



function App() {
    
    const OpenMenu= () => {
        document.querySelector(".sidebar").classList.add("open");
    };
    const CloseMenu= () => {
        document.querySelector(".sidebar").classList.remove("open");
    };
  return (
<BrowserRouter>      

    <div className="grid-container">
        <header className="header">
            <div className="type">
                <button onClick= {OpenMenu}>
                    &#9776;
                </button>
                <a href="index.html">Food Ordering</a>
            </div>
            <div className="header-link">
                <a href="cart.html"> Cart </a>
                <a href="signin">Sign In </a>
            </div>
        </header>
        <aside className="sidebar">
            <h3>MENU CATEGORIES</h3>
            <button className="sidebar-close-button" onClick={CloseMenu}>x</button>
            <ul>
                <li>
                    <a href="index.html">Non-veg</a>
                </li>

                <li>
                    <a href="index.html">Veg</a>
                </li>
            
            
            </ul>
        </aside>
        <main className="main"/>
            <div className="content"/>
            <Route path="/menu/:id" component={MenuScreen} />
            <Route path="/" exact={true}component={HomeScreen} />
                <ul className="menus">
                    {
                        data.menus.map(menu =>
                            <li>
                            <div className="menu">
                                <img className="menu-image" src="/images/img3.jpg" alt="menu" />
                                <div className="menu-name">
                                    <a href="menu.html">{menu.name}</a>
                                </div>
                                <div className="menu-type">{menu.type}</div>
                                <div className="menu-price">Rs{menu.price}</div>
                                <div className="menu-rating">{menu.rating} Stars ({menu.numReviews})</div>
                            </div>
                        </li> )
                    }
                    
                    
               </ul>
                 <div>
               <footer className="footer" >
                 All Right Reserved.
               </footer>
           </div>
        </div>
        </BrowserRouter>        
    );

}

export default App;
